import React from 'react';
import './sign-up.css';
import content from '../../static/index';
import { useForm } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers';
import * as yup from 'yup';

const SignUp = () => {

    const { register, handleSubmit, formState: { errors } } = useForm();

    const onSubmit = (data) => console.log(data);
    return (
        <div>
            <h1>Don't have an account?</h1>
            <h3>Sign up here!</h3>
            <form onSubmit={handleSubmit(onSubmit)}>
                {content.inputs.map((input, key) => {
                    return (
                        <div key={key}>
                            <label htmlFor={input.name}>{input.label}</label>
                            <br />
                            <input type={input.type} name={input.name} {...register(input.name, 'formRequired')} {...register("formRequired", { requird: true })} />
                            { errors.formRequired && <span className='message'>This field is required</span>}
                            {/* <p className='message'>{errors.fullName?.message}</p> */}
                        </div>
                    )
                })}

                <label htmlFor='options'>User Type</label>
                <select if='options'>
                    <option value='Developer'>Developer</option>
                    <option value='Owner'>Owner</option>
                </select>

                <button>SIGN UP</button>
                <p>Have an account? Sign In</p>
            </form>
        </div>
    )
}

export default SignUp;
