import React, { Component } from 'react';
import Data from '../../static/data';
import './scrumboard.css'

export class ScrumBoard extends Component {

    state = Data;
    render() {
        console.log('logged in as', Data.inputs.map((name, id) => name.fullname));
        return (
            <div className='scrumboard'>
                <nav>
                    <h1>CHATSCRUM</h1>
                    {Data.inputs.map((input, id) => {
                        return (
                            <>
                                <div className='var' key={id}>
                                    <p>User type:{input.userType}</p>
                                    <p>Project Name:{input.projectType}</p>
                                </div>
                                <p id='info'>Hello {input.fullname}. Welcome to your scrumboard</p>
                            </>
                        )
                    })}
                </nav>
            </div>
        )
    }

}

export default ScrumBoard
