import React, { Component } from 'react';
import content from '../../static/index';
import './scrumboard.css'

export class ScrumBoard extends Component {

    state = Data
    render() {
        console.log('logged in as', content.inputs.fullname);
        return (
            <div className='scrumboard'>
                <nav>
                    <h1>CHATSCRUM</h1>
                    <div className='var'>
                        <p>User type:{content.userType.value}</p>
                        <p>Project Name:{content.otherInputs.text}</p>
                    </div>
                </nav>
                <p id='info'>Hello {content.fullname}. Welcome to your scrumboard</p>
            </div>
        )
    }
}

export default ScrumBoard
